import React, { ReactNode } from "react";
import { Streamlit, StreamlitComponentBase, withStreamlitConnection } from "streamlit-component-lib";

interface State {}

// Function to darken a HEX color with improved logic for robustness and simplicity
function darkenColor(color: string, percent: number): string {
  let num = parseInt(color.slice(1), 16),
      amt = Math.round(2.55 * percent),
      R = (num >> 16) - amt,
      G = ((num >> 8) & 0x00FF) - amt,
      B = (num & 0x0000FF) - amt;

  R = Math.max(Math.min(255, R), 0);
  G = Math.max(Math.min(255, G), 0);
  B = Math.max(Math.min(255, B), 0);

  return "#" + (R << 16 | G << 8 | B).toString(16).padStart(6, '0');
}

class MultiMenu extends StreamlitComponentBase<State> {
  // Declare the linkElement property for clean-up
  private linkElement: HTMLLinkElement | null = null;

  componentDidMount() {
    // Single point of adding the Google Fonts link to the document head
    if (!this.linkElement) {
      const link = document.createElement('link');
      link.href = 'https://fonts.googleapis.com/icon?family=Material+Icons';
      link.rel = 'stylesheet';
      document.head.appendChild(link);
      this.linkElement = link;
    }
  }

  componentWillUnmount() {
    // Clean-up: Remove the Google Fonts link
    if (this.linkElement) {
      document.head.removeChild(this.linkElement);
    }
  }

  private onSubMenuClicked = (subMenuName: string): void => {
    Streamlit.setComponentValue(subMenuName);
  };

  public render = (): ReactNode => {
    const {
      menu_titles = [],
      menu_titles_font_size,
      sub_menus = {},
      sub_menu_icons = {},
      use_container_width,
      sub_menu_color = '#F0F2F6', // Default color if sub_menu_color is undefined
      sub_menu_font_size,
      sub_menu_button_gap = 10,
      sub_menu_font_color = "#31333F",
    } = this.props.args;

    const componentStyle: React.CSSProperties = {
      display: "flex",
      flexDirection: "row",
      width: '100%',
    };

    const titleContainerStyle: React.CSSProperties = {
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      textAlign: 'center',
      flex: use_container_width ? 1 : undefined,
      margin: '0 5px',
    };

    const titleStyle: React.CSSProperties = {
      marginBottom: '5px',
      fontWeight: 'bold',
      fontSize: menu_titles_font_size,
    };

    const lineStyle: React.CSSProperties = {
      height: '1.25px',
      backgroundColor: '#BDBFC3',
      width: '100%',
    };

    return (
      <div style={componentStyle}>
        {menu_titles.map((title, index) => (
          <div key={title} style={titleContainerStyle}>
            <h3 style={titleStyle}>{title}</h3>
            <div style={lineStyle}></div>
            {sub_menus[title] && sub_menus[title].map((subMenu, subMenuIndex) => {
              const iconName = sub_menus[title][subMenuIndex] || '';

              return (
                <div key={`${title}-${subMenu}`} style={{ marginTop: sub_menu_button_gap, width:'100%'}}>
                  <button
                    onClick={() => this.onSubMenuClicked(subMenu)}
                    onMouseOver={(e) => {
                      const button = e.currentTarget;
                      button.style.backgroundColor = darkenColor(sub_menu_color, 20);
                      button.style.filter = 'brightness(80%)';
                    }}
                    onMouseOut={(e) => {
                      const button = e.currentTarget;
                      button.style.backgroundColor = sub_menu_color;
                      button.style.filter = 'none';
                    }}
                    style={{ 
                      cursor: "pointer", 
                      width: "100%", 
                      borderRadius: '8px',
                      backgroundColor: sub_menu_color,
                      color: sub_menu_font_color,
                      border: "none",
                      padding: '5px 5px',
                      fontSize: sub_menu_font_size,
                    }}
                  >
                    {iconName && (
                      <i className="material-icons" style={{verticalAlign: 'middle', marginRight: '8px'}}>
                        {iconName}
                      </i>
                    )}
                    {subMenu}
                  </button>
                </div>
              );
            })}
          </div>
        ))}
      </div>
    );
  };
}

export default withStreamlitConnection(MultiMenu);
