import {
  Streamlit,
  StreamlitComponentBase,
  withStreamlitConnection,
} from "streamlit-component-lib";
import React, { ReactNode } from "react";

interface State {}

// Function to darken a HEX color
// Corrected function with explicit types for parameters
function darkenColor(color: string, percent: number): string {
  const num = parseInt(color.replace("#", ""), 16),
        amt = Math.round(2.55 * percent),
        R = (num >> 16) - amt,
        G = (num >> 8 & 0x00FF) - amt,
        B = (num & 0x0000FF) - amt;
  return "#" + (0x1000000 + (R < 255 ? R < 1 ? 0 : R : 255) * 0x10000 + (G < 255 ? G < 1 ? 0 : G : 255) * 0x100 + (B < 255 ? B < 1 ? 0 : B : 255)).toString(16).slice(1);
}

class MyComponent extends StreamlitComponentBase<State> {
  public render = (): ReactNode => {
    const menu_titles = this.props.args["menu_titles"];
    const sub_menus = this.props.args["sub_menus"];
    const use_container_width = this.props.args["use_container_width"];
    const sub_menu_color = this.props.args["sub_menu_color"]; // Custom color provided by the user
    const sub_menu_font_size = this.props.args["sub_menu_font_size"]; 
    const sub_menu_font_color = this.props.args["sub_menu_font_color"] || "#31333F"; 
    const sub_menu_imgs = this.props.args["sub_menu_imgs"] || {};
    const safeSubMenuColor = sub_menu_color || '#F0F2F6'; // Default color if sub_menu_color is undefined
    const darkerSubMenuColor = darkenColor(safeSubMenuColor, 10);

    const componentStyle: React.CSSProperties = {
      display: "flex",
      flexDirection: "row",
      width: '100%',
     // Apply sans-serif font to the entire component
    };

    const titleContainerStyle: React.CSSProperties = {
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      textAlign: 'center',
      flex: use_container_width ? 1 : undefined,
      margin: '0 5px',
    };

    const titleStyle: React.CSSProperties = {
      marginBottom: '5px',
    };

    const lineStyle: React.CSSProperties = {
      height: '1.25px',
      backgroundColor: '#BDBFC3',
      width: '100%',
    };

    return (
      <div style={componentStyle}>
        {menu_titles.map((title: string, index: number) => (
          <div key={title} style={titleContainerStyle}>
            <h3 style={titleStyle}>{title}</h3>
            <div style={lineStyle}></div>
            {sub_menus[title] && sub_menus[title].length > 0 && 
            sub_menus[title].map((subMenu: string, subMenuIndex: number) => (
              <div key={subMenu} style={{ marginTop: '10px', width:'100%'}}>
                <button
                onMouseOver={(e) => {
                  const button = e.currentTarget;
                  button.style.backgroundColor = darkenColor(safeSubMenuColor, 20);
                  button.style.filter = 'brightness(10%)';
                  // Set the text glow effect
                  button.style.textShadow = '0 0 8px #ffffff';
                }}
                onMouseOut={(e) => {
                  const button = e.currentTarget;
                  button.style.backgroundColor = safeSubMenuColor;
                  button.style.filter = 'none';
                  // Remove the text glow effect
                  button.style.textShadow = 'none';
                }}

                  style={{ 
                    cursor: "pointer", 
                    width: "100%", 
                    borderRadius: '8px',
                    backgroundColor: sub_menu_color,
                    color: sub_menu_font_color,
                    border: "none",
                    padding: '5px 5px',
                    fontSize: sub_menu_font_size,
                    backgroundImage: sub_menu_imgs[title] && sub_menu_imgs[title][subMenuIndex] ? `url(${sub_menu_imgs[title][subMenuIndex]})` : 'none',
                    backgroundSize: 'cover', // Cover the entire button area
                    backgroundPosition: 'center', // Center the background image
                    backgroundRepeat: 'no-repeat', // Do not repeat the image

                    transition: 'background-color 0.05s, filter 0.05s, text-shadow 1.05s', // Include filter in the transition
                  }}
                >
                  {subMenu}
                </button>
              </div>
            ))}
          </div>
        ))}
      </div>
    );
  };

  private onSubMenuClicked = (subMenuName: string): void => {
    // Handle sub menu click
    Streamlit.setComponentValue(subMenuName);
  };
}

export default withStreamlitConnection(MyComponent);
