import {
  Streamlit,
  StreamlitComponentBase,
  withStreamlitConnection,
} from "streamlit-component-lib";
import React, { ReactNode } from "react";

interface State {}

class MyComponent extends StreamlitComponentBase<State> {
  public render = (): ReactNode => {
    const menu_titles = this.props.args["menu_titles"];
    const sub_menus = this.props.args["sub_menus"];
    const use_container_width = this.props.args["use_container_width"];

    // Use a pseudo-element or a border on the container to simulate a continuous line
    const titlesContainerStyle: React.CSSProperties = {
      display: "flex",
      flexDirection: "row",
      width: use_container_width ? "100%" : undefined,
      position: 'relative', // Required for absolute positioning of the pseudo-element or additional div
    };

    const titleStyle: React.CSSProperties = {
      margin: "0 20px",
      flex: use_container_width ? 1 : undefined,
      textAlign: "center",
      paddingBottom: '20px', // Space for the separation line
      boxSizing: 'border-box', // Ensure padding is included in the element's height
    };

    return (
      <div style={titlesContainerStyle}>
        {menu_titles.map((title: string, index: number) => (
          <div key={title} style={titleStyle}>
            <h3>{title}</h3>
            {/* Submenus are rendered here */}
            {sub_menus[title] && sub_menus[title].length > 0 && 
              sub_menus[title].map((subMenu: string) => (
                <div key={subMenu} style={{ marginTop: 10 }}>
                  <button
                    onClick={() => this.onSubMenuClicked(subMenu)}
                    style={{ cursor: "pointer", width: "100%" }}
                  >
                    {subMenu}
                  </button>
                </div>
              ))
            }
          </div>
        ))}
        <hr style={{ position: 'absolute', bottom: '0', left: 0, width: '100%', borderColor: '#ccc' }} /> {/* Continuous line */}
      </div>
    );
  };

  private onSubMenuClicked = (subMenuName: string): void => {
    // Handle sub menu click
    Streamlit.setComponentValue(subMenuName);
  };
}

export default withStreamlitConnection(MyComponent);
